import { createSlice } from "@reduxjs/toolkit";

const initialState = {
    ques: [], // array
    ans: [], // array of arrays
    options: [],
    multiple: [],
    title: "Untitled",
    description: "Description"
}
const quizSlice = createSlice({
    name: 'quiz',
    initialState,
    reducers: {
        AddTotal: (state, action) => {
            state.ques.push(action.payload.question);
            state.options.push(action.payload.option);
            state.ans.push(action.payload.ans);
            state.multiple.push(action.payload.multiple);
        },
        EditQues: (state, action) => {
            state.ques = action.payload.ques;
        },
        EditOptions: (state, action) => {
            state.options = action.payload.options;
        },
        EditAns: (state, action) => {
            state.ans = action.payload.ans;
        },
        EditMultiple: (state, action) => {
            state.multiple = action.payload.multiple;
        },
        AddTitle: (state, action) => {
            const title = action.payload.title;
            const description = action.payload.description;
            if(title) state.title = title;
            if(description) state.description = description;
        }
    }
});
export const { EditQues, EditOptions, EditAns, EditMultiple, AddTotal, AddTitle }  = quizSlice.actions;
export default quizSlice.reducer;
