import { useSelector } from "react-redux";

const Show = () => {
    let questions = useSelector(store => store.quiz.ques);
    let options = useSelector(store => store.quiz.options);
    let multiple = useSelector(store => store.quiz.choice);
    let title = useSelector(store => store.quiz.title);
    let description = useSelector(store => store.quiz.description);
    return (
        <>
            <div className="container-fluid d-flex flex-column justify-content-center align-items-center" style={{ height: '80vh'}}>
                <div className="d-flex flex-column w-50 card py-4 px-3 position-relative shadow" style={{backgroundColor: 'rgb(245, 245, 245)'}}>
                    <div className="w-100 ribbon rounded-top"></div>
                    <p className="text mb-3 fs-3" id="title">{ title }</p>
                    <p className="text mb-3 fs-3" id="desc">{ description }</p>
                </div>
            </div>
            <div className="container-fluid d-flex flex-column justify-content-center align-items-center">
                {questions.map((ques,index) => {
                    return (
                        <div className="d-flex flex-column w-50 card py-4 px-3 position-relative shadow ques-card mb-3">
                            <p className="fw-bold">{ques}?</p>
                            <div className="options d-flex flex-column w-25 align-items-start">
                                {options[index].map((option, idx) => {
                                    return (
                                        <div className="form-check m-2">
                                            <input type={multiple[index]} className="form-check-input" name="options" id={option} />
                                            <label htmlFor={option}>{option}</label>
                                        </div>
                                    );
                                })}
                            </div>
                        </div>
                    );
                })}
            </div>
        </>
    );
}
export default Show;
