import { createSlice } from "@reduxjs/toolkit";

const initialState = {
    ques: [],
    ans: [],
    option: [],
    title: none
}
const quizSlice = createSlice({
    name: 'quiz',
    initialState,
    reducers: {
        AddQues: (state, action) => {
            state.ques.push(action.payload.ques);
            state.ans.push(action.payload.ans);
            state.option.push(action.payload.option);
        }
    }
});
export const { AddQues }  = quizSlice.actions;
export default quizSlice.reducer;
