import { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { AddTotal } from "../features/quizSlice";

const Questions = () => {
    let questions = useSelector(store => store.quiz.ques);
    let options = useSelector(store => store.quiz.options);
    let ans = useSelector(store => store.quiz.ans);
    const [opt, setOpt] = useState([]);
    const dispatch = useDispatch();
    const EditQues = (e, index) => {
        console.log(e);
    }
    const AddOption = () => {
        const option = document.querySelector('#option').value;
        setOpt([...opt, option]);
    }
    const AddQuestion = () => {
        const question = document.querySelector('#ques').value;
        const option = opt;
        if (question.length === 0 || option.length === 0) {
            window.alert('Incomplete');
            return;
        }
        dispatch(AddTotal({question, option}));
    }
    return (
        <>
            <div className="container-fluid d-flex flex-column justify-content-center align-items-center" style={{ height: '80vh' }}>
                {questions.map((ques,index) => {
                    return (
                        <div className="d-flex flex-column w-50 card py-4 px-3 position-relative shadow ques-card">
                            <div className="input-group">
                                <label htmlFor="ques" className="input-group-text bg-white">Question: </label>
                                <input type="text" className="form-control" onChange={EditQues(index)} value={ques}/>
                            </div>
                            <div className="options d-flex flex-column w-25 align-items-center">
                                {options[index].map((option, idx) => {
                                    return (
                                        <div className="form-check m-2">
                                            <input type="radio" className="form-check-input" name="options" id={option} />
                                            <input type="text" className="form-control" value={option}/>
                                        </div>
                                    );
                                })}
                            </div>
                        </div>
                    );
                })}
                <div className="d-flex flex-column w-50 card py-4 px-3 position-relative shadow ques-card">
                    <div className="input-group">
                        <label htmlFor="ques" className="input-group-text bg-white">Question: </label>
                        <input type="text" className="form-control" id="ques"/>
                    </div>
                    <div className="options d-flex flex-column w-25 align-items-center">
                        {opt.map((option, index) => {
                            <div className="form-check m-2">
                                <input type="radio" className="form-check-input" name="options"/>
                                <input type="text" className="form-control" value={option} />
                            </div>
                        })}
                        <div className="form-check m-2">
                            <input type="radio" className="form-check-input" name="options"/>
                            <input type="text" className="form-control" id="option" />
                        </div>
                        <button type="button" className="btn btn-sm btn-success" onClick={AddOption}>Add Option</button>
                    </div>
                    <button type="button" className="btn btn-success mt-3 ms-auto" onClick={AddQuestion}>Add Question</button>
                </div>
            </div>
        </>
    );
}
export default Questions;
