import { useSelector } from "react-redux";

const Show = () => {
    let questions = useSelector(store => store.quiz.ques);
    let options = useSelector(store => store.quiz.options);
    let multiple = useSelector(store => store.quiz.choice);
    return (
        <>
            <div className="container-fluid d-flex flex-column justify-content-center align-items-center">
                {questions.map((ques,index) => {
                    return (
                        <div className="d-flex flex-column w-50 card py-4 px-3 position-relative shadow ques-card mb-3">
                            <p>{ques}?</p>
                            <div className="options d-flex flex-column w-25 align-items-center">
                                {options[index].map((option, idx) => {
                                    return (
                                        <div className="form-check m-2">
                                            <input type={multiple[index]} className="form-check-input" name="options" id={option} />
                                            <input type="text" className="form-control" value={option}/>
                                        </div>
                                    );
                                })}
                            </div>
                        </div>
                    );
                })}
            </div>
        </>
    );
}
export default Show;
